import React, { Component } from 'react'
import Navigation from '../../Components/Navigation/NavigationItems/Navigation'
import Footer from '../../Components/Footer/Footer'
import { Link } from 'react-router-dom'
import classes from './Home.css'
import Slider from '../Slider/Slider'

class Home extends Component {
  state = {
    featured: data.featuredItems,
    current: 0,
    currentItem: data.featuredItems[0]
    }
  //creating arrows for 
  forwardHandler = () => {
    if (this.state.currentItem <= 1) {
      let next = this.current[next] + 1
      let nextProduct = this.state.featured[next]
      this.setState({ current: next, currentItem: nextProduct })
    } else {
      let first = 0
      let firstItem = this.state.featured[0]
      this.setState({ current: first, currentItem: firstItem })
    }
   }

  backHandler = () => {
    if (this.state.currentItem <= 1) {
      let next = this.current[next] + 1
      let nextProduct = this.state.featured[next]
      this.setState({ current: next, currentItem: nextProduct })
    } else {
      let first = 0
      let firstItem = this.state.featured[0]
      this.setState({ current: first, currentItem: firstItem })
    }

  }


  render() {
    return (
      <div>
        <Navigation />
        <header className="header-container">
          <div className="Hero">
            <p></p>
          </div>
        </header>
        {/* <main> */}
          <div className="items">
            <Link to="/products">
              <button type="button" className="btn">
                Order Now
              </button>
            </Link>
            <h1>Featured Items</h1>
          <Slider

          />        


          </div>
        {/* </main> */}
        <Footer />
      </div>
    )
  }
}

export default Home
